INSERT INTO `peliculas` (id, nombre, descripcion, ruta_imagen, estreno) VALUES (1, 'Los vengadores', 'El agente de S.H.I.E.L.D. Nick Furia ha reunido a un grupo de superhéroes para hacer frente a cualquier amenaza que llegue a la Tierra. El equipo lo forman Iron Man, Capitán América, Ojo de Halcón, Viuda Negra, Hulk y Thor.', 'febd5573-390f-49b6-9e15-68528cbc6dfa_losVengadores.png', false);
INSERT INTO `peliculas` (id, nombre, descripcion, ruta_imagen, estreno) VALUES (2, 'Jimmy neutron', 'Jimmy Neutrón es un niño genio que con sus inventos científicos hace su vida más entretenida. Se contacta con una raza extraterrestre y junto con su mejor amigo, Carl Wheezer, lanza un satélite en una tostadora. Luego de tener algunos inconvenientes con el aterrizaje, logra mandar el satélite y llegar a la escuela.', '75dd8cc2-fc8f-4d21-b8eb-78b46c520d39_jimmyNeutron.png', true);
INSERT INTO `peliculas` (id, nombre, descripcion, ruta_imagen, estreno) VALUES (3, 'Bob esponja', 'La existencia en Fondo Bikini, hogar de Bob Esponja y sus amigos, transcurre plácidamente hasta que todos ellos se embarcan en una odisea que los lleva a descubrir nuevos mundos y peligrosos monstruos.', '91016146-a663-4a1a-add7-12cb66a96061_bobEsponja.png', true);
INSERT INTO `peliculas` (id, nombre, descripcion, ruta_imagen, estreno) VALUES (4, 'Kimetsu no yaiba: Mugen Ressha-hen', 'Demon Slayer: Mugen Train cuenta la historia de Tanjiro Kamado y sus amigos de la Compañía de Cazadores de Demonios (Demon Slayer Corps). Juntos, se unen con uno de los mejores espadachines de la compañía, Kyojuro Rengoku, para investigar una misteriosa serie de desapariciones que han ocurrido dentro del “Tren Infinito”. Poco saben que Enmu, uno de los miembros de las Doce Lunas Demoníacas, también está a bordo y les ha preparado una trampa.', 'e6ab5166-ed95-4dff-bbb4-4fa1c5a60ee2_kimetsuNoYaiba.png', true);
INSERT INTO `peliculas` (id, nombre, descripcion, ruta_imagen, estreno) VALUES (5, '8 apellidos vascos', 'Rafa, un señorito andaluz que nunca ha salido de Sevilla, decide dejarlo todo para conquistar a Amaia, una chica vasca. Se muda al País Vasco y allí tendrá que adaptarse a un nuevo entorno y hacerse pasar por vasco para ganarse la aprobación del padre de Amaia.', 'aa9ae7da-367c-4365-b964-34aab94d8fd9_8ApellidosVascos.png', false);
